class PostComments{constructor(e){this.postId=e,this.postContainer=$("#post-"+e),this.newCommentForm=$(`#post-${e}-comments-form`),this.createComment(e);let t=this;$(" .delete-comment-button",this.postContainer).each((function(){t.deleteComment($(this))}))}createComment(e){let t=this;this.newCommentForm.submit((function(o){o.preventDefault();$.ajax({type:"post",url:"/comments/create",data:$(this).serialize(),success:function(o){let n=t.newCommentDOM(o.data.comment);$("#post-comments-"+e).prepend(n),t.deleteComment($(" .delete-comment-button",n)),new ToggleLike($(" .toggle-like-button",n)),new Noty({theme:"relax",text:"Comment published!",type:"success",layout:"topCenter",timeout:1500}).show()},error:function(e){console.log(e.responseText)}}),this.reset()}))}newCommentDOM(e){return $(`<li id="comment-${e._id}"> \n                    ${e.content} by : \n                    <em>you</em>\n                    <a class = "delete-comment-button" href="/comments/destroy/${e._id}"> <button type="button" class="btn btn-outline-danger"><em>Remove</em></button></a>\n                    <a class = "toggle-like-button" data-likes="${e.likes.length}"\n                    href="/likes/toggle/?id=${e._id}&type=Comment"><i class="fa-thumbs-up"></i> \n                    ${e.likes.length} Likes\n                    </a>\n                    </li>`)}deleteComment(e){$(e).click((function(t){t.preventDefault(),$.ajax({type:"get",url:$(e).prop("href"),success:function(e){$("#comment-"+e.data.comment_id).remove(),new Noty({theme:"relax",text:"Comment Deleted",type:"success",layout:"topRight",timeout:1500}).show()},error:function(e){console.log(e.responseText)}})}))}}