class ToggleLike{constructor(t){this.toggler=t,this.toggleLike()}toggleLike(){$(this.toggler).click((function(t){t.preventDefault();let e=this;$.ajax({type:"post",url:$(e).attr("href"),success:function(t){let a=parseInt($(e).attr("data-likes")),s="far";1==t.data.deleted?(a-=1,s="far"):(a+=1,s="fas"),$(e).attr("data-likes",a),$(e).html(`<i class="${s} fa-thumbs-up fa-lg"> </i>${a} Likes`)},error:function(t){console.log("Error",t)}})}))}}